/* istanbul ignore file */

import { ApiProperty } from '@nestjs/swagger';
import { BigNumber } from '@polymeshassociation/polymesh-sdk';
import { IsOptional, IsString } from 'class-validator';

import { ToBigNumber } from '~/common/decorators/transformation';
import { IsBigNumber } from '~/common/decorators/validation';

export class AuditorVerifySenderProofDto {
  @ApiProperty({
    description: 'Amount to be matched in the sender proof. Null value will not match anything',
    example: '1000',
    nullable: true,
    type: 'string',
  })
  @IsOptional()
  @ToBigNumber()
  @IsBigNumber()
  readonly amount: number | null;

  @ApiProperty({
    description: 'The id of the auditor',
    example: '1',
    type: 'string',
  })
  @ToBigNumber()
  @IsBigNumber()
  readonly auditorId: BigNumber;

  @ApiProperty({
    description: 'The sender proof to be verified',
    example:
      '',
    type: 'string',
  })
  @IsString()
  readonly senderProof: string;
}
